import React from 'react'

class CreateTweet extends React.Component {
    constructor(props) {
        super(props)
        this.state = {
            tweetVal: "",
            tweetLen: 0,
        }
    }

    handleTweetInput(e) {
        this.setState({
            tweetVal: e.target.value,
            tweetLen: e.target.value.length,
        })
    }

    render() {
        const { tweetVal, tweetLen } = this.state
        return (
            <div className="tweet-box">
                <textarea id="tweet-input"
                          name="tweet"
                          cols="30"
                          rows="10"
                          onChange={(e) => this.handleTweetInput(e)}
                          // value={tweetVal}
                          placeholder="What you have in mind.."
                />

            </div>
        );
    }
}

export default CreateTweet
