import React from 'react'

class CreateTweet extends React.Component {
    constructor(props) {
        super(props)
        this.state = {
            tweetVal: "",
            tweetLen: 0,
        }
    }
    render() {
        return (
            <div>
                <textarea style={{resize: none}} name="tweet" cols="30" rows="10" ></textarea>
            </div>
        );
    }
}

export default CreateTweet
