import React from "react"

class TweetManager extends React.Component {
    constructor(props) {
        super(props)
        this.state = {
            tweets: [],
            onPostTweet
        }
    }

    onPostTweet(tweet) {
        this.setState(prevState => {
           return { tweets: [tweet, ...prevState.tweets] }
        })
    }
}

export default TweetManager
