import React from 'react'

class CreateTweet extends React.Component {
    constructor(props) {
        super(props)
        this.state = {
            tweetVal: "",
            tweetLen: 0,
        }
    }
    render() {
        return (
            <div>
                <textarea id="tweet-input"
                          name="tweet"
                          cols="30"
                          rows="10"
                          placeholder="What you have in mind.."
                ></textarea>
            </div>
        );
    }
}

export default CreateTweet
